<?php

declare(strict_types = 1);

namespace App\Console\Commands\Shop;

use App\Helpers\DeepSeekHelper;
use App\Helpers\StringHelper;
use App\Modules\Shop\Models\ShopProduct;
use App\Modules\Shop\Models\ShopReview;
use App\Modules\ShopArticle\Models\ShopArticle;
use Carbon\Carbon;
use Illuminate\Console\Command;
use Illuminate\Support\Collection;
use Illuminate\Support\Facades\Log;


class GenArticlesCommand extends Command
{
    /**
     * The name and signature of the console command.
     *
     * @var string
     */
    protected $signature = 'shop:genArticles';

    /**
     * The console command description.
     *
     * @var string
     */
    protected $description = 'Command description';

    public function __construct(
        private readonly StringHelper $stringHelper,
        private readonly DeepSeekHelper $deepSeekHelper
    ) {
        parent::__construct();
    }

    /**
     * Execute the console command.
     */
    public function handle()
    {
        $logMessage = '[' . now()->toDateTimeString() . '] Command shop:genArticles executed' . PHP_EOL;
        file_put_contents(storage_path('logs/genArticlesRequest.log'), $logMessage, FILE_APPEND);

        $numArticles = 8;
        $symbolsAmount = 8000;
        $reviewsLimit = 150;

        $output = '';

        $product = ShopProduct::query()
            ->where('epn_month_income', '>', 2000)
            ->where('epn_cashback', '>', 200)
            ->whereNull('articles_created_at')
            ->where('reviews_amount', '>', 100)
            ->orderBy('epn_month_income', 'desc')
            ->first();

        if (!$product) {
            $this->info('no products for article');

            return 0;
        }

        $reviews = ShopReview::query()
            ->where('product_id', $product->id)
            ->where('grade', '>', 2)
            ->orderBy('sort')
            ->limit($reviewsLimit)
            ->get();

        if (!$reviews || count($reviews) < 50) {
            $this->info('Not enough reviews');

            return 0;
        }

        $articles = ShopArticle::query()
            ->select(['site_id'])
            ->where('product_id', $product->id)
            ->where('code', 'like', 'review-%')
            ->limit($reviewsLimit)
            ->get()
            ->toArray();

        //var_dump(config('sites'));die;

        $nextSitesIds = (function(array $existsIdList, int $numArticles) {
            $sites = config('sites');
            $allIdList = [];
            foreach ($sites as $s) {
                if ($s['group'] == 'shop') {
                    $allIdList[] = $s['id'];
                }
            }
            $rest = array_diff($allIdList, $existsIdList);
            return array_slice($rest, 0, $numArticles);;
        })(array_column($articles, 'site_id'), $numArticles);

        $numArticles = count($nextSitesIds);

        if ($numArticles == 0) {
            $product->update(['articles_created_at' => Carbon::now()]);
            $this->info('All articles created for product_id=' . $product->id);
            return 0;
        }

        $message = $this->getMessage($product, $reviews, $symbolsAmount, $numArticles);

        $dsResponse = $this->deepSeekHelper->chat($message);
        $content = $dsResponse['choices'][0]['message']["content"] ?? null;

//        $dsResponse = '[]';
//        $content = $this->getContent();

        $content = str_replace(["\r\n", "\n", "\r"], '', $content);

        //file_put_contents(storage_path() . '/test.txt', $content);

        $contentData = [];


        $content = (function($content) {
            $startPos = strpos($content, '```json');
            if ($startPos === false) return $content;

            $startPos += strlen('```json'); // –ü—Ä–æ–ø—É—Å–∫–∞–µ–º —Å–∞–º –º–∞—Ä–∫–µ—Ä
            $endPos = strpos($content, '```', $startPos);

            return ($endPos !== false)
                ? trim(substr($content, $startPos, $endPos - $startPos))
                : substr($content, $startPos);
        })($content);



        try {
            if (empty($content)) {
                throw new \Exception('Empty content');
            }
            $contentData = json_decode($content, true);
//var_dump('contentData:', $contentData);
            if (empty($contentData) || !is_array($contentData)) {
                throw new \Exception('Not valid json');
            }

            if (count($contentData) < $numArticles) {
                throw new \Exception('Not enough articles generated: ' . count($contentData) . '<' . $numArticles);
            }

            Log::channel('deepseek_requests')->info(PHP_EOL.PHP_EOL . '---' . PHP_EOL . 'DeepSeek API Request:', [
                'count' => count($contentData),
                'request' => $message,
                'response' => $dsResponse,
            ]);


        } catch (\Exception $e) {
            $error = $e->getMessage();
            Log::channel('deepseek_errors')->error('DeepSeek API Error:', [
                'message' => $error,
                'request' => $message,
                'response' => $dsResponse,
            ]);
            $this->info(PHP_EOL.PHP_EOL . '---' . PHP_EOL . 'Error: ' . $error);
            return 0; // –í–æ–∑–≤—Ä–∞—â–∞–µ–º 0, –µ—Å–ª–∏ –∫–æ–º–∞–Ω–¥–∞ –≤—ã–ø–æ–ª–Ω–µ–Ω–∞ —É—Å–ø–µ—à–Ω–æ
        }

        $i = 0;
        foreach ($contentData as $c) {
            $siteId = $nextSitesIds[$i];
            $i++;
            ShopArticle::create(
                [
                    'site_id' => $siteId,
                    'product_id' => $product->id,
                    'name' => $product->title_ae,
                    'h1' => $c['h1'] ?? null,
                    'title' => $c['title'] ?? null,
                    'keywords' => $c['keywords'] ?? null,
                    'description' => $c['description'] ?? null,
                    'uri' => $this->stringHelper->buildUri($c['h1'] ?: $product->title_ae),
                    'code' => 'review-' . $product->id,
                    'text' => [
                        [
                            'type' => 'product',
                            'data' => [
                                'text' => null,
                                'id' => $product->id,
                                'title' => $product->title_ae,
                                'props' => $c['props'] ?? null,
                                'cons' => $c['cons'] ?? null
                            ]
                        ],
                        [
                            'type' => 'visual',
                            'data' => [
                                'text' => $c['text']
                            ]
                        ]
                    ],
                    'note' => 'deepseek',
                    'published_at' => $this->genPublishDate($siteId)
                ]
            );
        }



        $output .= 'id = ' . $product->id . '; count =' . count($contentData) . ' ; title_ae=' . $product->title_ae
            . ' sites_id: ' . implode(',', $nextSitesIds) ;

        $this->info($output);
        return 0; // –í–æ–∑–≤—Ä–∞—â–∞–µ–º 0, –µ—Å–ª–∏ –∫–æ–º–∞–Ω–¥–∞ –≤—ã–ø–æ–ª–Ω–µ–Ω–∞ —É—Å–ø–µ—à–Ω–æ
    }

    /**
     *
     * @param int $siteId - –æ—Ç 2 –¥–æ 9
     * @return Carbon
     */
    private function genPublishDate(int $siteId): ?Carbon
    {
        if ($siteId == 2) {
            return Carbon::now();
        }

        if ($siteId == 8 || $siteId == 9) {
            return null;
        }

        // –î–ª—è siteId 3-8 –≥–µ–Ω–µ—Ä–∏—Ä—É–µ–º —Å–ª—É—á–∞–π–Ω—É—é –¥–∞—Ç—É –≤ –ø—Ä–µ–¥–µ–ª–∞—Ö +2 –º–µ—Å—è—Ü–µ–≤
        $now = Carbon::now();
        $endDate = $now->copy()->addMonths(2);

        // –†–∞–∑–Ω–∏—Ü–∞ –≤ —Å–µ–∫—É–Ω–¥–∞—Ö –º–µ–∂–¥—É —Ç–µ–∫—É—â–µ–π –¥–∞—Ç–æ–π –∏ –∫–æ–Ω–µ—á–Ω–æ–π –¥–∞—Ç–æ–π
        $diffInSeconds = $now->diffInSeconds($endDate);

        // –ì–µ–Ω–µ—Ä–∏—Ä—É–µ–º —Å–ª—É—á–∞–π–Ω–æ–µ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ —Å–µ–∫—É–Ω–¥ –≤ —ç—Ç–æ–º –¥–∏–∞–ø–∞–∑–æ–Ω–µ
        $randomSeconds = rand(0, (int) $diffInSeconds);

        return $now->addSeconds($randomSeconds);
    }


    private function getMessage(ShopProduct $product, Collection $reviews, int $symbolsAmount, int $numArticles): string
    {
        $tagsText = '';
        if (isset($product->extra_data['reviews']['tags'])) {
            $tagsText = '---
        –î–∞–Ω–Ω—ã–µ –æ —Ç–æ–º —á—Ç–æ –ª—é–¥–∏ —á–∞—â–µ –≤—Å–µ–≥–æ —É–ø–æ–º–∏–Ω–∞—é—Ç –≤ –æ—Ç–∑—ã–≤—Ö:
        ';
            foreach ($product->extra_data['reviews']['tags'] as $tag) {
                $tagsText .= $tag['title'] . ' - ' . $tag['counter'] . ' —Ä–∞–∑' . PHP_EOL;
            }
        }

        $reviewData = [];

        foreach ($reviews as $review) {
            $reviewData[] = [
                'reviewerName' => $review['reviewer']['name'],
                'date' => $review['date'],
                'grade' => $review['grade'],
                'likesAmount' => $review['likesAmount'],
                'text' => $review['text'],
                'additional' => $review['additional']
            ];
        }

        $reviewsText = json_encode($reviewData, JSON_UNESCAPED_UNICODE);

        $message = '

–ù–∞–ø–∏—à–∏ –æ–±–∑–æ—Ä –Ω–∞ —Ç–æ–≤–∞—Ä –Ω–µ –∏—Å–ø–æ–ª—å–∑—É—è –¥—Ä—É–≥–∏—Ö –¥–∞–Ω–Ω—ã—Ö, –∫—Ä–æ–º–µ –Ω–∏–∂–µ–ø—Ä–∏–≤–µ–¥–µ–Ω–Ω—ã—Ö. –û—Å–Ω–æ–≤—ã–≤–∞–π—Å—è –≤ –æ—Å–Ω–æ–≤–Ω–æ–º –Ω–∞ –æ—Ç–∑—ã–≤–∞—Ö. –°—Ç–∞—Ä–∞–π—Å—è –±–æ–ª—å—à–µ —Å—Å—ã–ª–∞—Ç—å—Å—è –Ω–∞ –∫–∞–∫–∏–µ-—Ç–æ –æ—Ç–∑—ã–≤—ã –ø—Ä–∏ –∞–≥—Ä—É–º–µ–Ω—Ç–∞—Ü–∏–∏.
–ù–µ –∑–∞–±—ã–≤–∞–π –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å –∏–Ω–æ—Ñ—Ä–º–∞—Ü–∏—é –∏–∑ –¥–∞–Ω–Ω—ã—Ö –æ —Ç–æ–º —á—Ç–æ –ª—é–¥–∏ —á–∞—â–µ –≤—Å–µ–≥–æ —É–ø–æ–º–∏–Ω–∞—é—Ç –≤ –æ—Ç–∑—ã–≤—Ö
–û–ø—Ç–∏–º–∏–∑–∏—Ä—É–π —Ç–µ–∫—Å—Ç –ø–æ–¥ –Ω–∏–∑–∫–æ—á–∞—Å—Ç–æ—Ç–Ω—ã–µ –∑–∞–ø—Ä–æ—Å—ã, —á—Ç–æ–±—ã –æ–±–∑–æ—Ä –±—ã–ª –≤ —Ç–æ–ø–µ –≤—ã–¥–∞—á–∏ –ø–æ–∏—Å–∫–æ–≤–∏–∫–æ–≤ –ø–æ –Ω–∏–º.
–û—Ñ–æ—Ä–º–∏ –≤ –≤–∏–¥–µ —Å—Ç–∞—Ç—å–∏ —Å –∑–∞–≥–æ–ª–æ–≤–∫–æ–º, –≤–≤–µ–¥–µ–Ω–∏–µ–º –∏ —Ç–¥. (–Ω–µ –¥–∞–µ–ª–∞–π —Å—Å—ã–ª–∫–∏ –≤ –≤–∏–¥–µ —Ü–∏—Ñ—Ä, –∫–æ–≥–¥–∞ –æ—Ç–∑—ã–≤—ã —Ü–∏—Ç–∏—Ä—É–µ—à—å). –ù–µ –º–µ–Ω–µ–µ 6 –ø–æ–¥—Ä–∞–∑–¥–µ–ª–æ–≤. –ö–∞–∂–¥—ã–π –ø–æ–¥—Ä–∞–∑–¥–µ–ª –Ω–µ –º–µ–Ω–µ–µ 800 —Å–∏–º–≤–æ–ª–æ–≤(–±—É–∫–≤)
–û—Ç–¥–µ–ª—å–Ω–æ —Å—Ñ–æ—Ä–º–∏—Ä—É–π –º–µ—Ç–∞ —Ç–µ–≥–∏: title, description,keywords –∏ h1 –¥–ª—è —Å—Ç—Ä–∞–Ω–∏—Ü—ã (–Ω–∏–∂–µ –ø—Ä–∏–≤–µ–¥–µ–Ω–∞ —Å—Ç—Ä—É–∫—Ç—É—Ä–∞, –≤—Å—Ç–∞–≤—å –∏—Ö –≤ —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤—É—é—â–∏–µ –ø–æ–ª—è)
–°—Ç–∞—Ä–∞–π—Å—è —Å–¥–µ–ª–∞—Ç—å —Ç–µ–∫—Å—Ç —É–Ω–∏–∫–∞–ª—å–Ω—ã–º –¥–ª—è –ø–æ–∏—Å–∫–æ–≤–∏–∫–æ–≤. –¢–µ–±–µ –Ω—É–∂–Ω–æ –±—É–¥–µ—Ç —Å–æ—Å—Ç–∞–≤–∏—Ç—å ' . $numArticles . ' —Å—Ç–∞—Ç—å–∏(—é)(–µ–π) –Ω–∞ –æ–¥–Ω—É —Ç–µ–º—É, –Ω–æ –∫–∞–∂–¥–∞—è –∏–∑ –Ω–∏—Ö –¥–æ–ª–∂–Ω–∞ –±—ã—Ç—å —É–Ω–∏–∫–∞–ª—å–Ω–∞ –¥–ª—è –ø–æ–∏—Å–∫–æ–≤–∏–∫–æ–≤. (–†–∞–Ω—å—à–µ –≤–µ—Ä–æ—è—Ç–Ω–æ —É–∂–µ –±—ã–ª–∏ –ø–æ–¥–±–Ω—ã–π –∑–∞–ø—Ä–æ—Å –æ—Ç –º–µ–Ω—è –Ω–∞ —Å—Ç–∞—Ç—å—é –Ω–∞ –¥–∞–Ω–Ω—é—É —Ç–µ–º—É, –ø–æ—ç—Ç–æ–º—É, –µ—Å–ª–∏ –º–æ–∂–Ω–æ, —Ç–æ–∂–µ —É—á–∏—Ç—ã–≤–∞–π —ç—Ç–æ –∏ –Ω–µ –≥–µ–Ω–µ—Ä–∏—Ä—É–π –¥—É–±–ª–∏–∫–∞—Ç —Ç.–µ. –Ω–µ –±–µ—Ä–∏ –∏–∑ –∫–µ—à–∞ —Ä—É–∑—É–ª—å—Ç–∞—Ç)
–¢–∞–∫–∂–µ –≤—ã–¥–µ–ª–∏ –∫—Ä–∞—Ç–∫–æ –æ—Å–Ω–æ–≤–Ω—ã–µ –ø–ª—é—Å—ã –∏ –º–∏–Ω—É—Å—ã. 5-10 –ø–ª—é—Å–æ–≤ ($props) –∏ 1-4 –º–∏–Ω—É—Å–∞ ($cons) –≤ –ø–∞—Ä–∞–º–µ—Ç—Ä–∞—Ö props –∏ cons —Å—Ç—É—Ä—É—Ç—É—Ä—ã –æ—Ç–≤–µ—Ç–∞
–¶–µ–ª—å —Å—Ç–∞—Ç—å–∏ - –ø—Ä–æ–¥–∞—Ç—å —Ç–æ–≤–∞—Ä, –ø–æ—ç—Ç–æ–º—É –±–æ–ª—å—à–µ –∞–∫—Ü–µ–Ω—Ç–∏—Ä—É–π –≤–Ω–∏–º–∞–Ω–∏–µ –Ω–∞ –¥–æ—Å—Ç–æ–∏–Ω—Å—Ç–≤–∞—Ö –∏ –ø–æ–º–µ–Ω—å—à–µ –≤—ã–¥–µ–ª—è–π –Ω–µ–¥–æ—Å—Ç–∞—Ç–∫–∏ –∏ –∫–∞–∫-—Ç–æ —Å–º—è–≥—á–∞–π –∏—Ö.

–¢–µ–∫—Å—Ç —Å—Ç–∞—Ç—å–∏ –æ—Ñ–æ–º—Ä–ª—è–π —Ç–æ—á–Ω–æ —Ç–∞–∫ –∂–µ –∫–∞–∫ —Ç—ã —ç—Ç–æ –¥–µ–ª–∞–µ—à—å –≤ –≤–µ–± –≤–µ—Ä—Å–∏–∏ —Å –ø–æ–¥–∑–∞–≥–æ–ª–æ–≤–∫–∞–º–∏, hr, utf-8 —Å–∏–º–≤–æ–ª–∞–º–∏-–∫–∞—Ä—Ç–∏–Ω–∫–∞–º–∏ (–æ—á–µ–Ω—å –∂–µ–ª–∞—Ç–µ–ª—å–Ω–∞ –≤—Å—Ç–∞–≤–∫–∞ —Ç–∞–∫–∏—Ö utf-8 —Å–∏–º–≤–æ–ª–æ–≤) –∏ —Ç–¥. –í–Ω–∏–º–∞–Ω–∏–µ, —Ç–æ–ª—å–∫–æ –≤ —Ç–µ–∫—Å—Ç–µ —Å—Ç–∞—Ç—å–∏! –í –æ—Å—Ç–∞–ª—å–Ω—ã—Ö –º–µ—Å—Ç–∞—Ö –±–µ–∑ —ç—Ç–æ–≥–æ

–ü—Ä–∏ –≥–µ–Ω–µ—Ä–∞—Ü–∏–∏ –Ω–µ –∏—Å–ø–æ–ª—å–∑—É–π —Å–∏–º–≤–æ–ª—ã: "\r\n", "\n", "\r"

–°—Ä—É–∫—Ç—É—Ä–∞ –æ—Ç–≤–µ—Ç–∞ –¥–ª—è –æ–¥–Ω–æ–≥–æ —ç–ª–µ–º–µ–Ω—Ç–∞ (–¥–ª—è –æ–¥–Ω–æ–π —Å—Ç–∞—Ç—å–∏):

{
    "product_id": "$product_id"
    "name" : "$name",
    "h1": "$h1",
    "title": "$title",
    "description": "$description",
    "keywords": "$keywords",
    "props": "$props"
    "cons": "$cons"
    "text": "$text"
}

–ò—Ç–æ–≥–æ–≤—ã–π –æ—Ç–≤–µ—Ç –¥–æ–ª–∂–µ–Ω –ø—Ä–µ–¥—Å—Ç–∞–≤–ª—è—Ç—å —Å–æ–±–æ–π json (json –¥–æ–ª–∂–µ–Ω –±—ã—Ç—å –≤–∞–ª–∏–¥–Ω—ã–º - —Å–±–∞–ª–∞–Ω—Å–∏—Ä–æ–≤–∞–Ω–Ω—ã–µ –∫–∞–≤—ã—á–∫–∏, —ç–∫—Ä–∞–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ –∏ —Ç–¥.) –∏–∑ ' . $numArticles . ' —Ç–∞–∫–∏—Ö —ç–ª–µ–º–µ–Ω—Ç–æ–≤: [{"product_id":"...", "name": "...",....},...,{...}]
–í–Ω–∏–º–∞–Ω–∏–µ ! –í –æ—Ç–≤–µ—Ç–µ –≤ —Ä–∞–∑–¥–µ–ª–µ message.content –¥–æ–ª–∂–µ–Ω –±—ã—Ç—å –≤–∞–ª–∏–¥–Ω—ã–π Json –±–µ–∑ –ª–∏—à–Ω–∏—Ö —Å–ª–æ–≤ ["choices"][0]["message"]["content"] = "[{"product_id":"...", "name": "...",....},...,{...}]"

$name - –Ω–∞–∑–≤–∞–Ω–∏–µ —Ç–æ–≤–∞—Ä–∞, –æ–Ω–æ –æ–¥–Ω–æ –¥–ª—è –≤—Å–µ—Ö: "' . $product->title_ae . '"
$product_id - id —Ç–æ–≤–∞—Ä–∞, –æ–¥–Ω–æ –¥–ª—è –≤—Å–µ—Ö: ' . $product->id . '
–í—Å–µ –æ—Å—Ç–∞–ª—å–Ω—ã–µ –ø–∞—Ä–∞–º–µ—Ç—Ä—ã –∏–Ω–¥–∏–≤–∏–¥—É–∞–ª—å–Ω—ã –¥–ª—è –∫–∞–∂–¥–æ–π —Å—Ç–∞—Ç—å–∏.
$h1 - h1 –¥–ª—è —Å—Ç—Ä–∞–Ω–∏—Ü—ã
$title, $description, $keyworks - –º–µ—Ç–∞ —Ç–µ–≥–∏ –≤ head.
$text - —Ç–µ–∫—Å—Ç (html) —Å–∞–º–æ–π —Å—Ç–∞—Ç—å–∏. –†–∞–∑–º–µ—Ä —Å—Ç–∞—Ç—å–∏ –¥–æ–ª–∂–µ–Ω –±—ã—Ç—å –Ω–µ –º–µ–Ω–µ–µ ' . $symbolsAmount . ' —Å–∏–º–≤–æ–ª–æ–≤(–±—É–∫–≤). (–í–Ω–∏–º–∞–Ω–∏–µ. –≠—Ç–æ –æ—á–µ–Ω—å –≤–∞–∂–Ω–æ —á—Ç–æ–±—ã —Ä–∞–∑–º–µ—Ä –±—ã–ª –Ω–µ –º–µ–Ω–µ–µ  ' . $symbolsAmount . ' —Å–∏–º–≤–æ–ª–æ–≤)
$props - –ø—Ä–µ–∏–º—É—â–µ—Å—Ç–≤–∞ —Ç–æ–≤–∞—Ä–∞ (–ø—Ä–æ—Å—Ç–æ —Ç–µ–∫—Å—Ç, —Ä–∞–∑–¥–µ–ª–µ–Ω–Ω—ã–π –ø–µ—Ä–µ–≤–æ–¥–æ–º —Å—Ç—Ä–æ–∫–∏)
$cons - –Ω–µ–¥–æ—Å—Ç–∞—Ç–∫–∏ —Ç–æ–≤–∞—Ä–∞ (–ø—Ä–æ—Å—Ç–æ —Ç–µ–∫—Å—Ç, —Ä–∞–∑–¥–µ–ª–µ–Ω–Ω—ã–π –ø–µ—Ä–µ–≤–æ–¥–æ–º —Å—Ç—Ä–æ–∫–∏)

–í—Ö–æ–¥–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ:

$product_id: ' . $product->id . '
---
$name: ' . $product->title_ae . '
---
–•–∞—Ä–∞–∫—Ç–µ—Ä–∏—Å—Ç–∏–∫–∏:

' . $product->characteristics . '

' . $tagsText . '

---
–û—Ç–∑—ã–≤—ã:

' . $reviewsText . PHP_EOL;

        return $message;
    }

    public function getContent(): string
    {
        return "```json
[
    {
        \"product_id\": \"70233\",
