<?php

declare(strict_types = 1);

namespace App\Console\Commands\Shop;

use App\Helpers\DeepSeekHelper;
use App\Helpers\StringHelper;
use App\Modules\Shop\Models\ShopProduct;
use App\Modules\Shop\Models\ShopReview;
use App\Modules\ShopArticle\Models\ShopArticle;
use Carbon\Carbon;
use Illuminate\Console\Command;
use Illuminate\Support\Collection;
use Illuminate\Support\Facades\Log;


class GenArticlesCommand extends Command
{
    /**
     * The name and signature of the console command.
     *
     * @var string
     */
    protected $signature = 'shop:genArticles';

    /**
     * The console command description.
     *
     * @var string
     */
    protected $description = 'Command description';

    public function __construct(
        private readonly StringHelper $stringHelper,
        private readonly DeepSeekHelper $deepSeekHelper
    ) {
        parent::__construct();
    }

    /**
     * Execute the console command.
     */
    public function handle()
    {
        $logMessage = '[' . now()->toDateTimeString() . '] Command shop:genArticles executed' . PHP_EOL;
        file_put_contents(storage_path('logs/genArticlesRequest.log'), $logMessage, FILE_APPEND);

        $numArticles = 8;
        $symbolsAmount = 8000;
        $reviewsLimit = 150;

        $output = '';

        $product = ShopProduct::query()
            ->where('epn_month_income', '>', 2000)
            ->where('epn_cashback', '>', 200)
            ->whereNull('articles_created_at')
            ->where('reviews_amount', '>', 100)
            ->orderBy('epn_month_income', 'desc')
            ->first();

        if (!$product) {
            $this->info('no products for article');

            return 0;
        }

        $reviews = ShopReview::query()
            ->where('product_id', $product->id)
            ->where('grade', '>', 2)
            ->orderBy('sort')
            ->limit($reviewsLimit)
            ->get();

        if (!$reviews || count($reviews) < 50) {
            $this->info('Not enough reviews');

            return 0;
        }

        $articles = ShopArticle::query()
            ->select(['site_id'])
            ->where('product_id', $product->id)
            ->where('code', 'like', 'review-%')
            ->limit($reviewsLimit)
            ->get()
            ->toArray();

        //var_dump(config('sites'));die;

        $nextSitesIds = (function(array $existsIdList, int $numArticles) {
            $sites = config('sites');
            $allIdList = [];
            foreach ($sites as $s) {
                if ($s['group'] == 'shop') {
                    $allIdList[] = $s['id'];
                }
            }
            $rest = array_diff($allIdList, $existsIdList);
            return array_slice($rest, 0, $numArticles);;
        })(array_column($articles, 'site_id'), $numArticles);

        $numArticles = count($nextSitesIds);

        if ($numArticles == 0) {
            $product->update(['articles_created_at' => Carbon::now()]);
            $this->info('All articles created for product_id=' . $product->id);
            return 0;
        }

        $message = $this->getMessage($product, $reviews, $symbolsAmount, $numArticles);

        $dsResponse = $this->deepSeekHelper->chat($message);
        $content = $dsResponse['choices'][0]['message']["content"] ?? null;

        $content = $this->getContent();
        $content = str_replace(["\r\n", "\n", "\r"], '', $content);
        //file_put_contents(storage_path() . '/test.txt', $content);

        $contentData = [];

//        $content = str_replace('```json', '', $content);
//        $content = str_replace('```', '', $content);
//        $content = trim($content);

        $content = (function($content) {
            $startPos = strpos($content, '```json');
            if ($startPos === false) return $content;

            $startPos += strlen('```json'); // Пропускаем сам маркер
            $endPos = strpos($content, '```', $startPos);

            return ($endPos !== false)
                ? trim(substr($content, $startPos, $endPos - $startPos))
                : substr($content, $startPos);
        })($content);



        try {
            if (empty($content)) {
                throw new \Exception('Empty content');
            }
            $contentData = json_decode($content, true);
//var_dump('contentData:', $contentData);
            if (empty($contentData) || !is_array($contentData)) {
                throw new \Exception('Not valid json');
            }

            if (count($contentData) < $numArticles) {
                throw new \Exception('Not enough articles generated');
            }

            Log::channel('deepseek_requests')->info('DeepSeek API Request:', [
                'count' => count($contentData),
                'request' => $message,
                'response' => $dsResponse,
            ]);


        } catch (\Exception $e) {
            $error = $e->getMessage();
            Log::channel('deepseek_errors')->error('DeepSeek API Error:', [
                'message' => $error,
                'request' => $message,
                'response' => $dsResponse,
            ]);
            $this->info('Error: ' . $error);
            return 0; // Возвращаем 0, если команда выполнена успешно
        }

        $i = 0;
        foreach ($contentData as $c) {
            $siteId = $nextSitesIds[$i];
            $i++;
            ShopArticle::create(
                [
                    'site_id' => $siteId,
                    'product_id' => $product->id,
                    'name' => $product->title_ae,
                    'h1' => $c['h1'] ?? null,
                    'title' => $c['title'] ?? null,
                    'keywords' => $c['keywords'] ?? null,
                    'description' => $c['description'] ?? null,
                    'uri' => $this->stringHelper->buildUri($c['h1'] ?: $product->title_ae),
                    'code' => 'review-' . $product->id,
                    'text' => [
                        [
                            'type' => 'product',
                            'data' => [
                                'text' => null,
                                'id' => $product->id,
                                'title' => $product->title_ae,
                                'props' => $c['props'] ?? null,
                                'cons' => $c['cons'] ?? null
                            ]
                        ],
                        [
                            'type' => 'visual',
                            'data' => [
                                'text' => $c['text']
                            ]
                        ]
                    ],
                    'note' => 'deepseek',
                    'published_at' => $this->genPublishDate($siteId)
                ]
            );
        }



        $output .= 'id = ' . $product->id . '; count =' . count($contentData) . ' ; title_ae=' . $product->title_ae
            . ' sites_id: ' . implode(',', $nextSitesIds) ;

        $this->info($output);
        return 0; // Возвращаем 0, если команда выполнена успешно
    }

    /**
     *
     * @param int $siteId - от 2 до 9
     * @return Carbon
     */
    private function genPublishDate(int $siteId): ?Carbon
    {
        if ($siteId == 2) {
            return Carbon::now();
        }

        if ($siteId == 8 || $siteId == 9) {
            return null;
        }

        // Для siteId 3-8 генерируем случайную дату в пределах +2 месяцев
        $now = Carbon::now();
        $endDate = $now->copy()->addMonths(2);

        // Разница в секундах между текущей датой и конечной датой
        $diffInSeconds = $now->diffInSeconds($endDate);

        // Генерируем случайное количество секунд в этом диапазоне
        $randomSeconds = rand(0, (int) $diffInSeconds);

        return $now->addSeconds($randomSeconds);
    }


    private function getMessage(ShopProduct $product, Collection $reviews, int $symbolsAmount, int $numArticles): string
    {
        $tagsText = '';
        if (isset($product->extra_data['reviews']['tags'])) {
            $tagsText = '---
        Данные о том что люди чаще всего упоминают в отзывх:
        ';
            foreach ($product->extra_data['reviews']['tags'] as $tag) {
                $tagsText .= $tag['title'] . ' - ' . $tag['counter'] . ' раз' . PHP_EOL;
            }
        }

        $reviewData = [];

        foreach ($reviews as $review) {
            $reviewData[] = [
                'reviewerName' => $review['reviewer']['name'],
                'date' => $review['date'],
                'grade' => $review['grade'],
                'likesAmount' => $review['likesAmount'],
                'text' => $review['text'],
                'additional' => $review['additional']
            ];
        }

        $reviewsText = json_encode($reviewData, JSON_UNESCAPED_UNICODE);

        $message = '

Напиши обзор на товар не используя других данных, кроме нижеприведенных. Основывайся в основном на отзывах. Старайся больше ссылаться на какие-то отзывы при агрументации.
Не забывай использовать инофрмацию из данных о том что люди чаще всего упоминают в отзывх
Оптимизируй текст под низкочастотные запросы, чтобы обзор был в топе выдачи поисковиков по ним.
Оформи в виде статьи с заголовком, введением и тд. (не даелай ссылки в виде цифр, когда отзывы цитируешь). Не менее 6 подразделов. Каждый подраздел не менее 800 символов(букв)
Отдельно сформируй мета теги: title, description,keywords и h1 для страницы (ниже приведена структура, вставь их в соответствующие поля)
Старайся сделать текст уникальным для поисковиков. Тебе нужно будет составить ' . $numArticles . ' статьи(ю)(ей) на одну тему, но каждая из них должна быть уникальна для поисковиков. (Раньше вероятно уже были подбный запрос от меня на статью на даннюу тему, поэтому, если можно, тоже учитывай это и не генерируй дубликат т.е. не бери из кеша рузультат)
Также выдели кратко основные плюсы и минусы. 5-10 плюсов ($props) и 1-4 минуса ($cons) в параметрах props и cons стурутуры ответа
Цель статьи - продать товар, поэтому больше акцентируй внимание на достоинствах и поменьше выделяй недостатки и как-то смягчай их.

Текст статьи офомрляй точно так же как ты это делаешь в веб версии с подзаголовками, hr, utf-8 символами-картинками (очень желательна вставка таких utf-8 символов) и тд. Внимание, только в тексте статьи! В остальных местах без этого

При генерации не используй символы: "\r\n", "\n", "\r"

Сруктура ответа для одного элемента (для одной статьи):

{
    "product_id": "$product_id"
    "name" : "$name",
    "h1": "$h1",
    "title": "$title",
    "description": "$description",
    "keywords": "$keywords",
    "props": "$props"
    "cons": "$cons"
    "text": "$text"
}

Итоговый ответ должен представлять собой json (json должен быть валидным - сбалансированные кавычки, экранирование и тд.) из ' . $numArticles . ' таких элементов: [{"product_id":"...", "name": "...",....},...,{...}]
Внимание ! В ответе в разделе message.content должен быть валидный Json без лишних слов ["choices"][0]["message"]["content"] = "[{"product_id":"...", "name": "...",....},...,{...}]"

$name - название товара, оно одно для всех: "' . $product->title_ae . '"
$product_id - id товара, одно для всех: ' . $product->id . '
Все остальные параметры индивидуальны для каждой статьи.
$h1 - h1 для страницы
$title, $description, $keyworks - мета теги в head.
$text - текст (html) самой статьи. Размер статьи должен быть не менее ' . $symbolsAmount . ' символов(букв). (Внимание. Это очень важно чтобы размер был не менее  ' . $symbolsAmount . ' символов)
$props - преимущества товара (просто текст, разделенный переводом строки)
$cons - недостатки товара (просто текст, разделенный переводом строки)

Входные данные:

$product_id: ' . $product->id . '
---
$name: ' . $product->title_ae . '
---
Характеристики:

' . $product->characteristics . '

' . $tagsText . '

---
Отзывы:

' . $reviewsText;

        return $message;
    }

    public function getContent(): string
    {
        return "[
  {
    \"product_id\": \"69368\",
    \"name\": \"Смартфон HONOR Magic7 Pro, 12/512ГБ, 16ГБ/1ТБ, global\",
    \"h1\": \"HONOR Magic7 Pro: Идеальный Флагман для Повседневной Эксплуатации\",
    \"title\": \"Обзор HONOR Magic7 Pro: Экран, Звук и Производительность | Отзывы Владельцев\",
    \"description\": \"Реальный опыт использования HONOR Magic7 Pro. Детальный разбор преимуществ: яркий экран, мощный звук, автономность и камера. Анализ отзывов покупателей.\",
    \"keywords\": \"HONOR Magic7 Pro отзывы, обзор смартфона, глобальная версия, автономность, камера, производительность\",
    \"props\": \"Яркий и плавный 120Гц экран\\nМощный стереозвук с эффектом присутствия\\nВысокая производительность в играх и мультизадачности\\nДлительная автономность до 2 дней\\nПрофессиональная камера с ИИ-обработкой\\nУдобный эргономичный дизайн\\nБыстрая зарядка 100Вт\\nГлобальная прошивка с Google сервисами\\nНадежная 3D-разблокировка по лицу\",
    \"cons\": \"Комплектный адаптер требует переходника\\nНекоторым пользователям камера кажется избыточно обработанной\",
    \"text\": \"<h2>✨ Введение: Флагман, Опередивший Ожидания</h2><hr>Смартфон HONOR Magic7 Pro — настоящий технологический шедевр, сочетающий инновации и практичность. Владельцы единодушно отмечают его превосходство в ключевых аспектах: от дисплея, который называют \\\"лучшим в классе\\\", до феноменальной автономности. Как пишет Maxim S.: \\\"Смартфон супер, кому нужна запись разговоров и блокировка звонков... звук объемный\\\". Этот обзор основан исключительно на реальном опыте пользователей, чтобы помочь вам сделать осознанный выбор.<p>📊 Анализ 78 отзывов показывает, что 92% покупателей оценили устройство на 5 звезд. Чаще всего упоминаются: отличный экран (11 раз), впечатляющий звук (9 раз) и шустрая работа (7 раз). Удобный размер и сбалансированность дизайна делают его комфортным для ежедневного использования. Перейдем к детальному разбору характеристик.</p><p>🌟 Особенность модели — глобальная версия для международного рынка (отмечена в 5 отзывах), что гарантирует полную совместимость с сервисами Google и отсутствие региональных ограничений. Как подтверждает Olga M.: \\\"Все гугл-сервисы уже есть, приложения спокойно устанавливаются из Google Play\\\".</p><h2>🖥 Дисплей: Погружение в Контент Без Компромиссов</h2><hr>Экран HONOR Magic7 Pro получил максимальное количество положительных оценок. Пользователи отмечают его яркость, плавность и безопасность для зрения. \\\"Экран здесь честные 120Гц (даже на рабочем столе)\\\", — восхищается Vitaly B., сравнивая с конкурентами. Технология LTPO обеспечивает адаптивную частоту обновления, экономя заряд батареи без потери визуального комфорта.<p>🔬 Особое внимание уделено защите от мерцания (PWM). Kirill B. отмечает: \\\"После Samsung глаза не болят, как после Самсунга note 20ultra\\\". Это подтверждают лабораторные тесты — коэффициент шим-пульсации ниже 5%, что соответствует стандартам комфортного просмотра. Дисплей сохраняет читаемость под прямым солнцем благодаря пиковой яркости 1600 нит, о чем пишет Tony Z.: \\\"Экран лучше — глаза не болят\\\".</p><p>🌈 Цветопередача реалистична без избыточной насыщенности. Пользователи, перешедшие с iPhone (как Alexander K.), отмечают естественность оттенков: \\\"Экран очень и очень качественный с защитой для зрения\\\". Диагональ 6.8\\\" с минимальными рамками создает эффект полного погружения при просмотре фильмов или играх.</p><h2>🔊 Аудиосистема: Кинотеатр в Вашем Кармане</h2><hr>Звук — вторая по популярности хвалебная тема в отзывах. Стереодинамики с технологией DTS:X создают эффект объемного звучания. \\\"Звук и вправду силен. Как через динамики телефона, так и через Bluetooth\\\", — делится впечатлениями Alexander K. При прослушивании музыки детализация сохраняется даже на высокой громкости.<p>🎧 Для меломанов важна работа с наушниками. Чипсет Snapdragon 8 Gen 3 обеспечивает поддержку кодеков aptX HD и LDAC. Пользователь Vadim A. подчеркивает: \\\"В машине будто подключил через ЦАП\\\". Система адаптирует эквалайзер под тип контента — от подкастов до экшен-игр.</p><p>🔇 Отдельного внимания заслуживает шумоподавление в микрофонах. Nikolay P. подтверждает: \\\"Шумоподавление в видеосъёмке шикарное решение\\\". Три направленных микрофона эффективно отсекают ветер и фоновые шумы, делая голос в записях кристально чистым.</p><h2>⚡ Производительность: Мощность Без Тормозов</h2><hr>Процессор Snapdragon 8 Gen 3 в сочетании с 12/16 ГБ оперативной памяти обеспечивает молниеносную работу. \\\"Телефон летает во всех смыслах\\\", — резюмирует Arslanbek K. В тестах AnTuTu устройство набирает свыше 1.5 млн баллов, опережая многих конкурентов.<p>🎮 Геймеры ценят стабильность кадровой частоты. Slava B. тестировал в PUBG: \\\"Игра летает без тормозов\\\". Система охлаждения с паровой камерой предотвращает троттлинг даже в ресурсоемких играх. Поддержка технологии GPU Turbo оптимизирует графическую нагрузку, сохраняя плавность геймплея.</p><p>🧠 Искусственный интеллект — ключевая особенность MagicOS. Kirill B. описывает фишки: \\\"Фишки искусственного интеллекта тоже прикольная хреновина... удаление объектов шикарная вещь\\\". Нейросеть ускоряет обработку фото, оптимизирует энергопотребление и улучшает работу голосового ассистента.</p><h2>🔋 Автономность: Два Дня Без Розетки</h2><hr>Батарея ёмкостью 5800 мАч — лидер в классе флагманов. Petr M. делится результатами: \\\"Вчера как получил в обед, сразу зарядил, сегодня второй день уже, а ещё 74 процента\\\". Технология Silicon-Carbon продлевает срок службы аккумулятора, сохраняя ёмкость после 800 циклов зарядки.<p>⚡ Поддержка быстрой зарядки 100Вт — решение для активных пользователей. Stanislav G. подтверждает: \\\"До 100% зарядился за 45 минут\\\". Беспроводная зарядка 50Вт (в версии Pro) позволяет быстро восполнить заряд без проводов. Система оптимизации Honor OS анализирует использование приложений, экономя энергию на фоновых процессах.</p><p>🔋 Важный нюанс: некоторые пользователи получали устройство разряженным. Как объясняет Kirill B.: \\\"Это версия перепак, ее вскрывают и активируют для использования\\\". Это не влияет на дальнейшую работу батареи — после полного цикла зарядки автономность соответствует заявленной.</p><h2>📷 Камера: Профессиональные Снимки на Автомате</h2><hr>Тройная камера с основным сенсором 50 Мп — гордость модели. Roman G. сравнивает: \\\"Шикарная камера (до этого был oneplus 9 pro и разница чувствуется)\\\". Система стабилизации OIS+EIS гарантирует четкие кадры даже при съемке с рук.<p>🖼️ ИИ-обработка — палка о двух концах. С одной стороны, Renat M. отмечает: \\\"Пытается иногда пририсовать щетину\\\". С другой — технология улучшает детализацию в сложных условиях. Galina K. довольна результатом: \\\"Делает снимки качественные\\\". Для профессионалов предусмотрен ручной режим Pro с настройкой параметров ISO и выдержки.</p><p>🎥 Видео 8К@30fps впечатляет динамическим диапазоном. Пользователь Vadim A. тестировал в путешествиях: \\\"Брал его в поставку в Стамбул, очень доволен камерой и съемкой видео\\\". Ночной режим сохраняет детализацию при освещенности